import { NextRequest, NextResponse } from 'next/server';
import { createClient } from '@supabase/supabase-js';
import { getAuthUser } from '@/lib/auth-server';
import {
  initTikTokVideoUpload,
  uploadTikTokVideoBytes,
  publishTikTokVideo,
  refreshTikTokToken
} from '@/lib/tiktok';

/**
 * POST /api/library/tiktok/publish
 * Publish video to TikTok
 *
 * Body:
 * - videoUrl: URL of video generated by Seedream I2V (required)
 * - caption: Video description
 * - hashtags: Array of hashtags
 */
export async function POST(req: NextRequest) {
  try {
    const { user, error: authError } = await getAuthUser();

    if (authError || !user) {
      return NextResponse.json(
        { ok: false, error: 'Non authentifié' },
        { status: 401 }
      );
    }

    const { videoUrl, caption, hashtags } = await req.json();

    if (!videoUrl) {
      return NextResponse.json(
        { ok: false, error: 'URL de la vidéo manquante' },
        { status: 400 }
      );
    }

    console.log('[TikTokPublish] Starting TikTok publication');
    console.log('[TikTokPublish] Video URL type:', typeof videoUrl);
    console.log('[TikTokPublish] Video URL length:', videoUrl?.length);
    console.log('[TikTokPublish] Video URL (full):', videoUrl);

    // Validate video URL format
    if (!videoUrl.startsWith('http://') && !videoUrl.startsWith('https://')) {
      console.error('[TikTokPublish] Invalid video URL format:', videoUrl);
      return NextResponse.json(
        { ok: false, error: 'URL de vidéo invalide. L\'URL doit commencer par http:// ou https://' },
        { status: 400 }
      );
    }

    // Initialize Supabase
    const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL!;
    const supabaseKey = process.env.SUPABASE_SERVICE_ROLE_KEY || process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!;
    const supabase = createClient(supabaseUrl, supabaseKey);

    // Get user's TikTok credentials
    const { data: profile, error: profileError } = await supabase
      .from('profiles')
      .select('tiktok_access_token, tiktok_refresh_token, tiktok_token_expiry, tiktok_username')
      .eq('id', user.id)
      .single();

    if (profileError || !profile?.tiktok_access_token) {
      return NextResponse.json(
        { ok: false, error: 'Compte TikTok non connecté' },
        { status: 400 }
      );
    }

    // Check if token needs refresh
    let accessToken = profile.tiktok_access_token;
    const tokenExpiry = new Date(profile.tiktok_token_expiry);
    const now = new Date();

    if (tokenExpiry <= now) {
      console.log('[TikTokPublish] Token expired, refreshing...');

      const clientKey = process.env.TIKTOK_CLIENT_KEY!;
      const refreshedTokens = await refreshTikTokToken(
        profile.tiktok_refresh_token,
        clientKey
      );

      // Update tokens in database
      const newExpiry = new Date();
      newExpiry.setSeconds(newExpiry.getSeconds() + refreshedTokens.expires_in);

      await supabase
        .from('profiles')
        .update({
          tiktok_access_token: refreshedTokens.access_token,
          tiktok_refresh_token: refreshedTokens.refresh_token,
          tiktok_token_expiry: newExpiry.toISOString(),
        })
        .eq('id', user.id);

      accessToken = refreshedTokens.access_token;
      console.log('[TikTokPublish] Token refreshed');
    }

    console.log('[TikTokPublish] Downloading video...');

    // Download video (generated by Seedream I2V or existing video)
    let videoBuffer: Buffer;

    try {
      // Check if URL is from Supabase Storage
      const isSupabaseUrl = videoUrl.includes(supabaseUrl) || videoUrl.includes('supabase.co');

      if (isSupabaseUrl) {
        // Extract file path from Supabase URL
        // URL format: https://{project}.supabase.co/storage/v1/object/public/{bucket}/{path}
        const match = videoUrl.match(/\/object\/public\/([^\/]+)\/(.+)/);

        if (match) {
          const bucket = match[1];
          const filePath = match[2];

          console.log('[TikTokPublish] Downloading from Supabase Storage:', { bucket, filePath });

          // Download using Supabase client
          const { data: fileData, error: downloadError } = await supabase.storage
            .from(bucket)
            .download(filePath);

          if (downloadError) {
            console.error('[TikTokPublish] Supabase download error:', downloadError);
            throw new Error(`Supabase download failed: ${downloadError.message}`);
          }

          if (!fileData) {
            throw new Error('No data returned from Supabase Storage');
          }

          videoBuffer = Buffer.from(await fileData.arrayBuffer());
          console.log('[TikTokPublish] Downloaded from Supabase:', {
            size: (videoBuffer.length / 1024 / 1024).toFixed(2) + ' MB'
          });
        } else {
          throw new Error('Invalid Supabase URL format');
        }
      } else {
        // External URL (e.g., Seedream) - use fetch
        console.log('[TikTokPublish] Downloading from external URL');

        const videoResponse = await fetch(videoUrl, {
          headers: {
            'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36'
          }
        });

        if (!videoResponse.ok) {
          throw new Error(`HTTP ${videoResponse.status}: ${videoResponse.statusText}`);
        }

        videoBuffer = Buffer.from(await videoResponse.arrayBuffer());
      }
    } catch (fetchError: any) {
      console.error('[TikTokPublish] Download error:', {
        message: fetchError.message,
        stack: fetchError.stack,
        url: videoUrl
      });
      throw new Error(`Erreur lors du téléchargement de la vidéo: ${fetchError.message}`);
    }

    console.log('[TikTokPublish] Video downloaded:', {
      size: (videoBuffer.length / 1024 / 1024).toFixed(2) + ' MB'
    });

    // Step 1: Initialize TikTok video upload
    console.log('[TikTokPublish] Initializing TikTok upload...');

    const uploadInit = await initTikTokVideoUpload(
      accessToken,
      videoBuffer.length,
      videoBuffer.length, // chunk_size = total size (single chunk)
      1 // total_chunk_count = 1
    );

    console.log('[TikTokPublish] Upload initialized:', {
      publish_id: uploadInit.publish_id
    });

    // Step 2: Upload video bytes
    console.log('[TikTokPublish] Uploading video bytes...');

    await uploadTikTokVideoBytes(uploadInit.upload_url, videoBuffer);

    console.log('[TikTokPublish] Video uploaded');

    // Step 3: Publish video
    console.log('[TikTokPublish] Publishing video...');

    const publishResult = await publishTikTokVideo(
      accessToken,
      uploadInit.publish_id
    );

    console.log('[TikTokPublish] Video published:', {
      publish_id: publishResult.publish_id
    });

    // Format caption with hashtags
    const finalCaption = caption
      ? caption + '\n\n' + (hashtags || []).map((tag: string) => '#' + tag).join(' ')
      : (hashtags || []).map((tag: string) => '#' + tag).join(' ');

    // Save to tiktok_posts table
    const { error: insertError } = await supabase
      .from('tiktok_posts')
      .insert({
        id: publishResult.publish_id,
        user_id: user.id,
        video_description: finalCaption,
        posted_at: new Date().toISOString(),
        cached_video_url: videoUrl, // Store video URL
        synced_at: new Date().toISOString()
      });

    if (insertError) {
      console.error('[TikTokPublish] Warning: Failed to save to database:', insertError);
      // Don't fail the request, post was still published
    }

    console.log('[TikTokPublish] Post published successfully');

    return NextResponse.json({
      ok: true,
      post: {
        id: publishResult.publish_id,
        caption: finalCaption,
        posted_at: new Date().toISOString()
      }
    });

  } catch (error: any) {
    console.error('[TikTokPublish] Error:', error);
    return NextResponse.json(
      { ok: false, error: error.message || 'Erreur lors de la publication' },
      { status: 500 }
    );
  }
}
