import { NextRequest, NextResponse } from 'next/server';
import { createClient } from '@supabase/supabase-js';
import { getAuthUser } from '@/lib/auth-server';
import {
  initTikTokVideoUpload,
  uploadTikTokVideoBytes,
  publishTikTokVideo,
  publishTikTokVideoFromUrl,
  publishTikTokVideoViaFileUpload,
  refreshTikTokToken
} from '@/lib/tiktok';

/**
 * POST /api/library/tiktok/publish
 * Publish video to TikTok
 *
 * Body:
 * - videoUrl: URL of video generated by Seedream I2V (required)
 * - caption: Video description
 * - hashtags: Array of hashtags
 */
export async function POST(req: NextRequest) {
  try {
    const { user, error: authError } = await getAuthUser();

    if (authError || !user) {
      return NextResponse.json(
        { ok: false, error: 'Non authentifi√©' },
        { status: 401 }
      );
    }

    const { videoUrl, caption, hashtags } = await req.json();

    if (!videoUrl) {
      return NextResponse.json(
        { ok: false, error: 'URL de la vid√©o manquante' },
        { status: 400 }
      );
    }

    console.log('[TikTokPublish] Starting TikTok publication');
    console.log('[TikTokPublish] Video URL type:', typeof videoUrl);
    console.log('[TikTokPublish] Video URL length:', videoUrl?.length);
    console.log('[TikTokPublish] Video URL (full):', videoUrl);

    // Validate video URL format
    if (!videoUrl.startsWith('http://') && !videoUrl.startsWith('https://')) {
      console.error('[TikTokPublish] Invalid video URL format:', videoUrl);
      return NextResponse.json(
        { ok: false, error: 'URL de vid√©o invalide. L\'URL doit commencer par http:// ou https://' },
        { status: 400 }
      );
    }

    // Initialize Supabase
    const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL!;
    const supabaseKey = process.env.SUPABASE_SERVICE_ROLE_KEY || process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!;
    const supabase = createClient(supabaseUrl, supabaseKey);

    // Get user's TikTok credentials
    const { data: profile, error: profileError } = await supabase
      .from('profiles')
      .select('tiktok_access_token, tiktok_refresh_token, tiktok_token_expiry, tiktok_username')
      .eq('id', user.id)
      .single();

    if (profileError || !profile?.tiktok_access_token) {
      return NextResponse.json(
        { ok: false, error: 'Compte TikTok non connect√©' },
        { status: 400 }
      );
    }

    // Check if token needs refresh
    let accessToken = profile.tiktok_access_token;
    const tokenExpiry = new Date(profile.tiktok_token_expiry);
    const now = new Date();

    if (tokenExpiry <= now) {
      console.log('[TikTokPublish] Token expired, refreshing...');

      const clientKey = process.env.TIKTOK_CLIENT_KEY!;
      const refreshedTokens = await refreshTikTokToken(
        profile.tiktok_refresh_token,
        clientKey
      );

      // Update tokens in database
      const newExpiry = new Date();
      newExpiry.setSeconds(newExpiry.getSeconds() + refreshedTokens.expires_in);

      await supabase
        .from('profiles')
        .update({
          tiktok_access_token: refreshedTokens.access_token,
          tiktok_refresh_token: refreshedTokens.refresh_token,
          tiktok_token_expiry: newExpiry.toISOString(),
        })
        .eq('id', user.id);

      accessToken = refreshedTokens.access_token;
      console.log('[TikTokPublish] Token refreshed');
    }

    // Format caption with hashtags
    const finalCaption = caption
      ? caption + '\n\n' + (hashtags || []).map((tag: string) => '#' + tag).join(' ')
      : (hashtags || []).map((tag: string) => '#' + tag).join(' ') || 'Video TikTok';

    console.log('[TikTokPublish] ===== STARTING TIKTOK PUBLICATION =====');
    console.log('[TikTokPublish] Method: FILE_UPLOAD (required for Supabase URLs)');
    console.log('[TikTokPublish] Video URL:', videoUrl);
    console.log('[TikTokPublish] Video URL domain:', new URL(videoUrl).hostname);
    console.log('[TikTokPublish] Caption length:', finalCaption.length, 'chars');
    console.log('[TikTokPublish] Caption preview:', finalCaption.substring(0, 100));
    console.log('[TikTokPublish] Hashtags:', hashtags);

    // Use FILE_UPLOAD method instead of PULL_FROM_URL
    // Required because Supabase Storage domain cannot be verified with TikTok
    // This method downloads the video and uploads it directly to TikTok servers
    let publishResult;
    try {
      publishResult = await publishTikTokVideoViaFileUpload(
        accessToken,
        videoUrl,
        finalCaption,
        {
          privacy_level: 'SELF_ONLY', // Required for Sandbox mode
          disable_duet: false,
          disable_comment: false,
          disable_stitch: false,
          video_cover_timestamp_ms: 1000
        }
      );
    } catch (publishError: any) {
      console.error('[TikTokPublish] ‚ùå PUBLICATION FAILED');
      console.error('[TikTokPublish] Error type:', publishError.constructor.name);
      console.error('[TikTokPublish] Error message:', publishError.message);
      console.error('[TikTokPublish] Error stack:', publishError.stack);

      // CRITICAL: ALWAYS show the exact TikTok error message
      // Remove all filtering to see what TikTok really says
      const userMessage = `‚ùå Erreur lors de la publication TikTok\n\nüîç Message exact de TikTok:\n${publishError.message}`;

      /* Commented out - we need to see the raw error first
      if (publishError.message.includes('chunk') || publishError.message.includes('size')) {
        ...
      */

      // Throw the raw error to see what TikTok actually says
      throw new Error(userMessage);
    }

    console.log('[TikTokPublish] Video published:', {
      publish_id: publishResult.publish_id
    });

    // Save to tiktok_posts table
    const { error: insertError } = await supabase
      .from('tiktok_posts')
      .insert({
        id: publishResult.publish_id,
        user_id: user.id,
        video_description: finalCaption,
        posted_at: new Date().toISOString(),
        cached_video_url: videoUrl, // Store video URL
        synced_at: new Date().toISOString()
      });

    if (insertError) {
      console.error('[TikTokPublish] Warning: Failed to save to database:', insertError);
      // Don't fail the request, post was still published
    }

    // Update any matching draft to 'published' category
    console.log('[TikTokPublish] Updating draft category to published...');
    const { error: draftUpdateError } = await supabase
      .from('tiktok_drafts')
      .update({
        category: 'published',
        status: 'published'
      })
      .eq('user_id', user.id)
      .eq('media_url', videoUrl);

    if (draftUpdateError) {
      console.error('[TikTokPublish] Warning: Failed to update draft:', draftUpdateError);
      // Don't fail the request, post was still published
    } else {
      console.log('[TikTokPublish] ‚úÖ Draft category updated to published');
    }

    console.log('[TikTokPublish] Post published successfully');

    return NextResponse.json({
      ok: true,
      post: {
        id: publishResult.publish_id,
        caption: finalCaption,
        posted_at: new Date().toISOString()
      }
    });

  } catch (error: any) {
    console.error('[TikTokPublish] Error:', error);
    return NextResponse.json(
      { ok: false, error: error.message || 'Erreur lors de la publication' },
      { status: 500 }
    );
  }
}
